/*
 * ─── Animation Timeline (30fps, 240 frames = 8s) ───
 *
 * 0-75:    Camel drawing reveal (circle expanding from head)
 * 30-100:  Arc draws from both sides
 * 85-145:  SMOK'YBILL letters spring in (staggered)
 * 135-165: Horizontal lines grow from center
 * 155-185: BURGERS fades in
 * 185-240: Hold complete logo
 */

export const SmokybillLogo: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // ── Camel: circle reveal from head position ──
  // Head is at approximately (380, 73) in SVG space
  const camelRadius = interpolate(frame, [0, 75], [0, 100], {
    extrapolateRight: "clamp",
    extrapolateLeft: "clamp",
    easing: Easing.out(Easing.cubic),
  });

  // ── Arc: wipe reveal from center outward ──
  const arcReveal = interpolate(frame, [30, 100], [0, 1], {
    extrapolateRight: "clamp",
    extrapolateLeft: "clamp",
    easing: Easing.out(Easing.cubic),
  });

  // ── SMOK'YBILL: staggered spring ──
  const getLetterOpacity = (index: number) => {
    const delay = 55 + index * 5;
    return spring({
      fps,
      frame: frame - delay,
      config: { damping: 15, stiffness: 100 },
      durationInFrames: 20,
    });
  };

  const getLetterY = (index: number) => {
    const delay = 55 + index * 5;
    const progress = spring({
      fps,
      frame: frame - delay,
      config: { damping: 15, stiffness: 100 },
      durationInFrames: 20,
    });
    return interpolate(progress, [0, 1], [8, 0]);
  };

  // ── Horizontal lines: grow from center ──
  const lineScale = interpolate(frame, [135, 165], [0, 1], {
    extrapolateRight: "clamp",
    extrapolateLeft: "clamp",
    easing: Easing.out(Easing.back(1.5)),
  });

  // ── BURGERS: fade in ──
  const burgersOpacity = interpolate(frame, [155, 185], [0, 1], {
    extrapolateRight: "clamp",
    extrapolateLeft: "clamp",
  });
  const burgersY = interpolate(frame, [155, 185], [5, 0], {
    extrapolateRight: "clamp",
    extrapolateLeft: "clamp",
    easing: Easing.out(Easing.cubic),
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: "white",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <svg
        viewBox="270 55 260 165"
        width="70%"
        height="70%"
        style={{ overflow: "visible" }}
      >
        <defs>
          {/* Camel reveal mask: expanding circle from head */}
          <clipPath id="camelRevealClip">
            <circle cx={380} cy={73} r={camelRadius} />
          </clipPath>

          {/* Arc reveal: rect growing from center outward */}
          <clipPath id="arcRevealClip">
            <rect
              x={400 - arcReveal * 130}
              y={50}
              width={arcReveal * 260}
              height={120}
            />
          </clipPath>

          {/* Lines reveal: scale from center */}
          <clipPath id="linesRevealClip">
            <rect
              x={400 - lineScale * 130}
              y={190}
              width={lineScale * 260}
              height={25}
            />
          </clipPath>
        </defs>

        {/* ── Camel ── */}
        <g clipPath="url(#camelRevealClip)">
          <path
            d={CAMEL_D}
            style={{ fill: GOLD, fillRule: "nonzero" }}
            transform="matrix(1.3333333,0,0,-1.3333333,379.9572,86.267467)"
          />
        </g>

        {/* ── Arc ── */}
        <g clipPath="url(#arcRevealClip)">
          <path
            d={LEFT_ARC_D}
            style={{ fill: GOLD, fillRule: "nonzero" }}
            transform="matrix(1.3333333,0,0,-1.3333333,472.17427,155.11693)"
          />
          <path
            d={RIGHT_ARC_D}
            style={{ fill: GOLD, fillRule: "nonzero" }}
            transform="matrix(1.3333333,0,0,-1.3333333,320.6652,155.11693)"
          />
        </g>

        {/* ── SMOK'YBILL ── */}
        {SMOKYBILL_LETTERS.map((letter, i) => (
          <g
            key={letter.id}
            style={{
              opacity: getLetterOpacity(i),
            }}
            transform={`translate(0, ${getLetterY(i)})`}
          >
            <path
              d={letter.d}
              style={{ fill: BROWN, fillRule: "nonzero" }}
              transform={letter.transform}
            />
          </g>
        ))}

        {/* ── Horizontal lines ── */}
        <g clipPath="url(#linesRevealClip)">
          <path
            d={LINE_SHORT_LEFT_D}
            style={{ fill: GOLD, fillRule: "nonzero" }}
            transform="matrix(1.3333333,0,0,-1.3333333,360.3832,196.44507)"
          />
          <path
            d={LINE_LONG_D}
            style={{ fill: GOLD, fillRule: "nonzero" }}
            transform="matrix(1.3333333,0,0,-1.3333333,472.2604,205.74987)"
          />
          <path
            d={LINE_SHORT_RIGHT_D}
            style={{ fill: GOLD, fillRule: "nonzero" }}
            transform="matrix(1.3333333,0,0,-1.3333333,472.2604,196.44507)"
          />
        </g>

        {/* ── BURGERS ── */}
        <g
          style={{ opacity: burgersOpacity }}
          transform={`translate(0, ${burgersY})`}
        >
          {BURGERS_LETTERS.map((letter) => (
            <path
              key={letter.id}
              d={letter.d}
              style={{ fill: DARK, fillRule: "nonzero" }}
              transform={letter.transform}
            />
          ))}
        </g>
      </svg>
    </AbsoluteFill>
  );
};
